(window.webpackJsonp=window.webpackJsonp||[]).push([[12],{479:function(t,p,v){"use strict";v.r(p);var _=v(29),s=Object(_.a)({},(function(){var t=this,p=t.$createElement,v=t._self._c||p;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("p",[t._v("时间：")]),t._v(" "),v("p",[t._v("始建于宋代以前，明末毁于战火。清嘉庆八年(1803) 重建。")]),t._v(" "),v("p",[t._v("地理位置：")]),t._v(" "),v("p",[t._v("四川成都岷江都江堰鱼嘴分水堤之上。")]),t._v(" "),v("p",[t._v("人文历史：")]),t._v(" "),v("p",[t._v("安澜桥是我国著名的五大古桥之一，横跨在内江和外江的分水处，是一座名播中外的古索桥。最早称绳桥或竹滕桥，这与它的材料有关。到了宋代，改称“评事桥”。明朝末年毁于战火。清嘉庆八年（公元1803年），何先德夫妇倡议修建竹索桥，以木板面，旁设扶栏，两岸行人可以安渡狂澜，故更名“安澜桥”；民间为纪念何氏夫妇，又称之为“夫妻桥”,何先生修的桥因无栏杆所以一人摔下水摔死,被官员处死,妻子为了为丈夫雪冤想尽办法修了栏杆。全长约500米，在鱼嘴处建立外江水闸，把桥下移100多米，将竹索改为钢索，乘托缆索的木桩桥墩改为钢筋混凝土桩，桥身也缩为240米。远看如飞虹挂空，又像渔人晒网，形式十分别致。漫步桥上，西望岷江穿山咆哮而来，东望灌渠纵横，都江堰工程的概貌及其作用，更是一目了然。但据《华阳国志.蜀志》记载李冰“能笮”。《水经注.江水》载“涪江有笮桥”，证明至少安澜桥的修建，不会晚于修筑都江堰的年代。“笮”意为竹索，这是川西古代索桥的主要建筑材料，故安澜索桥又被称为竹桥、绳桥、竹藤桥等。")]),t._v(" "),v("p",[t._v("其他：")]),t._v(" "),v("p",[t._v("安澜桥飞架岷江南北,是藏、汉、羌族人民联系的纽带，也是沟通岷江内、外江两岸的交通要道。1974年因兴建外江水闸,经国务院批准，将安澜索桥由鱼嘴分水堤处下移了一百多米,并改竹索为钢索,乘托缆索的木桩桥墩改为钢筋混凝土桩,桥身也缩为261米。而今漫步桥上,西望岷江穿山咆哮而来,东望灌渠纵横,都江堰工程的概貌及其作用,更是一目了然。")]),t._v(" "),v("p",[v("img",{attrs:{src:"/img/photo/1.jpg",alt:"安澜桥"}})]),t._v(" "),v("p",[v("img",{attrs:{src:"/img/photo/2.jpg",alt:"安澜桥"}})])])}),[],!1,null,null,null);p.default=s.exports}}]);