(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{506:function(t,p,v){"use strict";v.r(p);var _=v(29),s=Object(_.a)({},(function(){var t=this,p=t.$createElement,v=t._self._c||p;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("p",[t._v("时间：")]),t._v(" "),v("p",[t._v("清乾隆57年（1719年）")]),t._v(" "),v("p",[t._v("地理位置：")]),t._v(" "),v("p",[t._v("四川省绵阳市江油县青林口")]),t._v(" "),v("p",[t._v("人文历史：")]),t._v(" "),v("p",[t._v("1935年4月，红军长征途经江油青林口时，为了宣传党和红军的方针政策，刻写了大量石刻标语，现存12条（幅），这些标语一般刻在岩壁、石碑、石柱、石板上，有阴刻、阳刻、平槽。依书法分，有行书、楷书、隶书。排列上有横排、竖排、左读、右读。从内容上分，宣传政治、经济、军事、群团等六个方面：（1）宣传党和红军的性质任务；（2）宣传红军的政策法令；（3）揭露国民党军阀、地主、豪绅的罪恶；（4）宣传党和红军的抗日主张；（5）实行工农专政和发展苏区经济；（6）分化和争取白军士兵。如“红军是中国民族和劳苦民众的救星”“婚姻自由”等。")]),t._v(" "),v("p",[t._v("红军桥原名“合益桥”，始建于清乾隆57年（1719年），清光绪20年（1894年），当地商贾为解决场镇上的交通，繁荣商贸，集资又建桥楼，取“同舟共济，大家受益”之意，命名为“合益桥”。")]),t._v(" "),v("p",[t._v("1935年4月，中国工农红军第四方面军长征途经此地，建立各级苏维埃政权。红军撤离时，一位女红军干部因病不能随队撤退，便留在当地杨采发老大娘家休养。红军撤离后，国民党还乡团四处搜捕红军战士和苏维埃干部，杨大娘千方百计把女红军藏于夹墙中，不幸还是被还乡团查出来了，还乡团将女红军绑在合益桥上严刑拷打，女红军坚贞不屈，最后被还乡团杀害于合益桥上，壮烈牺牲。新中国成立后，江油县人民政府根据当地人民的要求，将合益桥改名为红军桥。")]),t._v(" "),v("p",[t._v("其他：")]),t._v(" "),v("p",[t._v("1976年12月，红军桥被江油县革命委员会列为江油县（现江油市）文物保护单位。1992年1月，江油市人民政府再次确定红军桥为江油市文物保护单位。2007年，被批准为四川省级文物保护单位。")]),t._v(" "),v("p",[v("img",{attrs:{src:"/img/photo/7.jpg",alt:"合益桥"}})]),t._v(" "),v("p",[v("img",{attrs:{src:"/img/photo/8.jpg",alt:"合益桥"}})]),t._v(" "),v("p",[v("img",{attrs:{src:"/img/photo/9.jpg",alt:"合益桥"}})])])}),[],!1,null,null,null);p.default=s.exports}}]);